mapper.insert:

    select id from tsys_function where `class` = #{clazz};


insert into tsys_function (
`id`,`uid`,`parentId`,`class`,`name`,`uiSref`,`order`,`icon`,`alias`,`isShow`,`project`
) value (
#{id},#{uid},#{parentId},#{clazz},#{name},#{uiSref},#{order},#{icon},#{alias},#{isShow},'${project.getKey()}'
) on duplicate key update `class` = #{clazz};
